function CoverMacro() {
    if (!actor) {
        ui.notifications.warn("You must have an actor selected.");
        return;
    }
    coverDialog(actor).render(true);
}

function coverDialog(pc) {

  const optsCover = [
      {name: "Lesser"},
      {name: "Standard"},
      {name: "Greater"},
      {name: "Clear"},
  ]    
  let coveringUp;

  let content = `<p><strong>Character:</strong> ${pc.name}<br/>`;
  content += '</p>';
  content += '<hr/>';
  content += '<div class="form-group">';
  content += '<p><label>Cover Type : </label><select id="coverType">';
  for (let i = 0 ; i < optsCover.length ; i++) {
    content += `<option value='${i}'`;
    content += `>${optsCover[i].name}</option>`;
  }
  content += '</select></p>';
  content += '</div>';

  return new Dialog({
    title: "Cover",
    content: content,
    buttons: {
      roll: {
        icon: "<i class='fas fa-check'></i>",
        label: "Activate",
        callback: () => coveringUp = true,
      },
      cancel: {
        icon: "<i class='fas fa-times'></i>",
        label: "Cancel",
        callback: () => coveringUp = false,
      },
    },
    default: "Activate",
    close: html => {
        if (coveringUp) {
            let chosenCover = optsCover[html.find('#coverType')[0].value];
            activateCover(chosenCover.name);
        }
    }
  });
}

async function activateCover(chosenCover) {
    let msgContent;
    let myCover;
    let takingCover = true;
    let lesserCover = 'Compendium.pf2e.equipment-effects.lKkQvMzNG55Yhyef';
    let standardCover = 'Compendium.pf2e.equipment-effects.I9lfZUiCwMiGogVi';
    let greaterCover = 'Compendium.pf2e.equipment-effects.dlxrYwsliiZbsTYB';
    let allCovers = [lesserCover, standardCover, greaterCover];
    switch(chosenCover) {
        case 'Clear':
            var i;
            for(i=0; i< allCovers.length; i++){
                let existing = token.actor.items.find(
                   (e) => e.type === 'effect' && e?.data?.flags?.core?.sourceId === allCovers[i],
                );
                if (existing) {
                    token.actor.deleteOwnedItem(existing.id);
                }
            }
            msgContent = "Clearing cover modifiers for ";
            takingCover = false;
            break;
        case 'Lesser':
            myCover = lesserCover;
            msgContent = "Granting lesser cover to ";
            break;
        case 'Standard':
            myCover = standardCover;
            msgContent = "Granting standard cover to ";
            break;
        case 'Greater':
            myCover = greaterCover;
            msgContent = "Granting greater cover to ";
            break;
        default:
    }


    if (takingCover) {
        const item = duplicate(await fromUuid(myCover));
        await token.actor.createEmbeddedDocuments('Item', [item]);    
    }

    msgContent = msgContent + actor.data.name

    let chatData = {
        user: game.user.id,
        content: msgContent,
        whisper: [game.user.id],
    };
    ChatMessage.create(chatData, {});
}


CoverMacro()
